import { Component } from '@angular/core';
import { ActivatedRoute, ParamMap } from '@angular/router';

@Component({
  selector: 'app-descricao-material',
  templateUrl: './descricao-material.component.html',
  styleUrls: ['./descricao-material.component.css']
})
export class DescricaoMaterialComponent {
  nome: string | null;
  descricao: string;

  constructor(private route: ActivatedRoute) { }

  ngOnInit() {

    this.route.paramMap.subscribe((params: ParamMap) => {
      this.nome = this.route.snapshot.paramMap.get("nome");
      if (this.nome === 'auditorio') {
        this.descricao = 'auditorio com capacidade para 200 pessoas!'
      }
      if (this.nome === 'projetor') {
        this.descricao = 'interface HDMI, áudio, entre outros.!'
      }
      if (this.nome === 'som') {
        this.descricao = 'microfones, caixas com bluetooth, entre outros!'
      }
      if (this.nome === 'eletronicos') {
        this.descricao = 'periféricos, cabos HDMI ou VGA, entre outros!'
      }
    });
  }
}
