import { Component } from '@angular/core';

@Component({
  selector: 'app-main-page',
  templateUrl: './main-page.component.html',
  styleUrls: ['./main-page.component.css']
})
export class MainPageComponent {

  fraseBoasVindas:string="seja bem-vindo(a)!";
  fraseSistemaAgendemento:string="sistema de agendamentos da UEG, Câmpus Nordeste/Formosa.";

}
<div class="container">
    <div class="row">
      <div class="col">
        1 of 2
      </div>
      <div class="col">
        2 of 2
      </div>
    </div>

  </div>


<p class="boasVindas">{{fraseBoasVindas}}</p>
<img src="assets/img/projetores.png" class="projetor">

<img src="assets/img/auditorio.png" class="auditorio">

<img src="assets/img/logo-estendida.png" class="logo">
<p class="fraseSistemaAgendamento">{{fraseSistemaAgendemento}}</p>
